import React, { useEffect, useState } from 'react'
//component imports
import Introduction from './Views/Introduction'
import HomePageRouter from './Views/HomePageRouter'
import Contact from './Views/Contact'
//other imports
import styled from 'styled-components'
import {
  BrowserRouter as Router,
  Route,
  Switch,
} from 'react-router-dom'


const Home: React.FC = () => {

  const [ likesDark, setLikesDark ] = useState(false);

  useEffect(() => {
    if (window.matchMedia('(prefers-color-scheme)').media !== 'not all') {
      setLikesDark(true)
    }
  }, [setLikesDark])
  const App: React.FC = () => {
    return (
      <Providers>
        <Router>
          <Switch>
            <Route path="/" exact>
              <Home />
            </Route>
            <Route path="/farms" exact>
              <Farms />
            </Route>
            <Route path="/farms/:farmId">
              <Farm />
            </Route>
            <Route path="/Vote" exact>
              <Vote />
            </Route>
            <Route path="/Adverts" exact>
              <Adverts />
            </Route>
            <Route path="/Stats" exact>
              <Stats />
            </Route>
          </Switch>
        </Router>
        <Disclaimer />
      </Providers>
    )
  }

  const Providers: React.FC = ({ children }) => {
    return (
      <ThemeProvider theme={theme}>
        {/* change the ChainId below here for the preffered network when testing, 1 main 3 ropsten */}
        <UseWalletProvider chainId={1}>
          <YamProvider>
            <TransactionProvider>
              <ModalsProvider>
                <FarmsProvider>
                  {children}
                </FarmsProvider>
              </ModalsProvider>
            </TransactionProvider>
          </YamProvider>
        </UseWalletProvider>
      </ThemeProvider>
    )
  }
