import React from 'react'
import styled from 'styled-components'
import { NavLink } from 'react-router-dom'

import house from '../Images/house.png'
import First_company from '../Images/First_company.png'

const Work: React.FC = () => {

    return (
        <StyledContainer>
            <StyledSectionsHolder>
                <StyledSections to='/' exact><img width="30px" height="30px" src={house} alt='house to show that this is a button to home' /></StyledSections>
            </StyledSectionsHolder>
            <StyledHeader>Work</StyledHeader>
            <StyledInnerContainer>
                <StyledInnerGrouping>
                <StyledImg src={First_company} alt="Iguana as an html element, the logo of Progressing Today." />
                <span>Company Name: 微進科技有限公司</span>
                <span>Position: Frontend Engineer</span>
                <span>Duration: 2020/02/05-present</span>
                <span>During my time at 微進科技有限公司(Progressing.Today), I used the <StyledExternalLink href="https://www.egret.com/">Egret Game Engine</StyledExternalLink></span>
                </StyledInnerGrouping>
            </StyledInnerContainer>
        </StyledContainer>
    )
}


const StyledContainer = styled.div`
display: flex;
flex-flow: column;
margin-left: auto;
margin-right: auto;
border-radius: 4px;
justify-content: center;
width: 80%;
height: 90%;
background-color: #006464;
`
const StyledInnerContainer = styled.div`
display: flex;
flex-flow: row;
margin-top: 40px;
justify-content: space-around;
justify-items: space-around;
width: 100%;
height: 45%;
`
const StyledImg = styled.img`
@media (min-width: 900px) {
    width: 100px;
    height: 100px;
    margin
  }
@media (max-width: 900px) {
    width: 60px;
    height: 60px;
  }
`
const StyledHeader = styled.h3`
    text-align: center;
`
const StyledSections = styled(NavLink)`
    height: 10%;
    width: 10%;
    padding:2%;
`
const StyledSectionsHolder = styled.nav`
@media (min-width: 900px){
        position: absolute;
    color:unset;
    text-decoration: none;
    top: 3%;
    height: 5%;
    width: 5%;
    display: flex;
        left: 3%;
    }
    @media (max-width: 900px){
        position: absolute;
    color:unset;
    text-decoration: none;
    top: 3%;
    height: 5%;
    width: 5%;
    display: flex;
        left: 2%;
    }
`

const StyledExternalLink = styled.a`
text-decoration: none;
color: unset;
border: 1px solid white;
border-radius: 8px;
padding: 1px;
&:hover {
    background-color: white;
    color: #006464;
}
`
// const StyledInfo = styled.span`

// `

const StyledInnerGrouping = styled.div`
width: 33%;
display: flex;
flex-direction: column;
align-items: center;
`

export default Work;
