import React, { useEffect, useState } from 'react'
import styled from 'styled-components'

import { NavLink } from 'react-router-dom'
import house from '../Images/house.png'

const DeFi: React.FC = () => {
    const [zomPrice, setZomPrice] = useState(0);
    const [shrimpPrice, setShrimpPrice] = useState(0);

    useEffect(() => {
        axios.get('https://api.coingecko.com/api/v3/simple/price?ids=Cethereum%2Czombie-finance&vs_currencies=usd').then((res) => {
          if (res.status === 200) {
            setZomPrice(Number(res.data['zombie-finance'].usd))
          }
        })
        axios.get('https://api.coingecko.com/api/v3/simple/price?ids=Cethereum%2Cshrimp-finance&vs_currencies=usd').then((res) => {
          if (res.status === 200) {
            setShrimpPrice(Number(res.data['shrimp-finance'].usd))
          }
        })
      }, [setZomPrice, setShrimpPrice])

    return (
        <StyledContainer>
        <StyledSectionsHolder>
        <StyledSections to='/' exact><img width="50px" height="50px" src={house} alt='house to show that this is a button to home'/></StyledSections>
        </StyledSectionsHolder>
        <StyledHeader>In order to view some of the data on these sites you will need <br/>
            to have an Ethereum compatible wallet such as <StyledExternalLink href="https://metamask.io/">MetaMask.</StyledExternalLink>
            </StyledHeader>
        <StyledInnerContainer>
        <StyledInnerGrouping>
        <StyledExternalLink href="https://stake.zombie.finance/">Zombie.Finance</StyledExternalLink>

        </StyledInnerGrouping>
        <StyledInnerGrouping>
        <StyledExternalLink href="https://shrimp.finance/">Shrimp.Finance</StyledExternalLink>
        </StyledInnerGrouping>
        </StyledInnerContainer>
        </StyledContainer>
    )
}

const StyledContainer = styled.div`
display: flex;
flex-flow: column;
margin-left: auto;
margin-right: auto;
border-radius: 4px;
justify-content: center;
width: 80%;
height: 90%;
background-color: #008B8B;
`

const StyledInnerContainer = styled.div`
display: flex;
flex-flow: row;
margin-top: 80px;
margin-left: auto;
margin-right: auto;
justify-content: center;
width: 80%;
height: 15%;
`

const StyledInnerGrouping = styled.div`
width: 33%;
height: 100px;
display: flex;
flex-direction: column;
align-items: center;
`

const StyledHeader = styled.h3`
    text-align: center;
`

const StyledSections = styled(NavLink)`
    height: 10%;
    width: 10%;
    padding:2%;
`

const StyledSectionsHolder = styled.nav`
    position: absolute;
    color:unset;
    text-decoration: none;
    top: 3%;
    left: 3%;
    height: 15%;
    width: 15%;
    display: flex;
`

const StyledExternalLink = styled.a`
text-decoration: none;
color: #8b0000;
`


export default DeFi
