import React, { useState, useEffect } from 'react'
import styled from 'styled-components'
import myTime from '../Images/Me_in_taiwan_collage.png'
import photography from '../Images/Photography_collage.png'


const Lightbox: React.FC = () => {
    const [i, seti] = useState(0);
    const [interval, setinterval] = useState(3000);

    const nameArr = ['My Time in Taiwan', 'Photography of mine']
    const photoArr = [myTime, photography]
    const altArr = ['A collage of my time in Taiwan', 'A collage of my photography and my star model']

    useEffect(() => {
        setInterval(function () {
            if (i === 1) {
                const plus = i - 1;
                seti(plus)
            } else {
                const plus = i + 1;
                seti(plus)
            }
        }, interval);
    }, [ i, seti, interval ])

    return (
        <StyledDiv>
            {i === 0 && <>
                <Styledh1>{nameArr[0]}</Styledh1>
                <StyledPhoto alt={altArr[0]} src={photoArr[0]} />
            </>}
            {i === 1 && <>
                <Styledh1>{nameArr[1]}</Styledh1>
                <StyledPhoto alt={altArr[1]} src={photoArr[1]} />
            </>}
            <Styledspan></Styledspan>
        </StyledDiv>
    )
}
const StyledDiv = styled.div`
    display: flex;
    flex-flow: column;
    align-items: center;
    justify-content: center;
`
const Styledh1 = styled.p`
    margin-top: 5%;
    margin-bottom: 0px;
    font-size: 34px;
`
const StyledPhoto = styled.img`
width: 256px;
`
const Styledspan = styled.span`
    display: flex;
    flex-flow: row;
`
const StyledLink = styled.a`
color: yellow;
font-size: 19px;
text-align: center;
`
const StyledSpacer = styled.div`
height: 15px;
width: 100%;
`
export default Lightbox
