import React, { useEffect, useState } from 'react'
//component imports
import Introduction from './Views/Introduction'
import DarkMode from './Views/DarkMode'
//other imports
import styled from 'styled-components'


const Home: React.FC = () => {

  const [ likesDark, setLikesDark ] = useState(false);

  useEffect(() => {
    if (window.matchMedia('(prefers-color-scheme)').media !== 'not all') {
      setLikesDark(true)
    }
  }, [setLikesDark])

  return (
    <>
    {likesDark ? 
    <StyledDiv className="body_dark">
      <Introduction/>
    </StyledDiv> 
    :
    <StyledDiv className="body_light">
      <Introduction/>
    </StyledDiv>
    }
    </>
  )
}

const StyledDiv = styled.div`
    display: flex:
    width: 80%;
    height: 100%;
    background-color: #F0F0F0;
`

export default Home
