import React, { useState, useEffect } from 'react'
import styled from 'styled-components'
import { NavLink } from 'react-router-dom'

import house from '../Images/house.png'
import myTime from '../Images/Me_in_taiwan_collage.png'
import photography from '../Images/Photography_collage.png'
import play from '../Images/play.png'
import pause from '../Images/pause.png'
import next from '../Images/next.png'
import previous from '../Images/previous.png'


const Lightbox: React.FC = () => {
    const [i, seti] = useState(0);
    const [interval, setinterval] = useState(3000);
    const [paused, setpaused] = useState(false);

    const nameArr = ['My Time in Taiwan', 'Photography of mine']
    const photoArr = [myTime, photography]
    const altArr = ['A collage of my time in Taiwan', 'A collage of my photography and my star model']

    const StopTimer = function () {
        if (paused === false) {
            setpaused(true)
        } else {
            setpaused(false)
        }
    }

    const Forward = function () {
        if (i < 1) {
            const plus = i + 1;
            seti(plus)
        }
    }

    const Backward = function () {
        if (i > 0) {
            const plus = i - 1;
            seti(plus)
        }
    }

    const Addtoi = function(){
        if (paused === false) {
            setInterval(function () {
                if (i === 1) {
                    const plus = i - 1;
                    seti(plus)
                } else {
                    const plus = i + 1;
                    seti(plus)
                }
            }, interval);
        }
    }

    useEffect(() => {
        Addtoi()
    })

    return (
        <StyledDiv>
            <StyledSectionsHolder>
                <StyledSections to='/' exact><img width="30px" height="30px" src={house} alt='house to show that this is a button to home' /></StyledSections>
            </StyledSectionsHolder>
                <div> className="rotating">
                <Styledh1>{nameArr[i]}</Styledh1>
                <StyledPhoto alt={altArr[i]} src={photoArr[i]} />
                </div>
            <StyledSpacer />
            <Styledspan>  <Styledbuttons onClick={Backward} src={previous} /> <Styledbuttons onClick={StopTimer} src={paused ? pause : play} /> <Styledbuttons onClick={Forward} src={next} /> </Styledspan>
        </StyledDiv>
    )
}
const StyledDiv = styled.div`
    display: flex;
    flex-flow: column;
    align-items: center;
    justify-content: center;
`
const Styledh1 = styled.p`
    margin-top: 5%;
    margin-bottom: 0px;
    font-size: 34px;
`
const StyledPhoto = styled.img`
width: 256px;
`
const Styledspan = styled.span`
    display: flex;
    flex-flow: row;
`
const Styledbuttons = styled.img`
    width: 25px;
`
const StyledSpacer = styled.div`
height: 15px;
width: 100%;
`
const StyledSections = styled(NavLink)`
    height: 10%;
    width: 10%;
    padding:2%;
`
const StyledSectionsHolder = styled.nav`
@media (min-width: 900px){
        position: absolute;
    color:unset;
    text-decoration: none;
    top: 3%;
    height: 15%;
    width: 15%;
    display: flex;
        left: 3%;
    }
    @media (max-width: 900px){
        position: absolute;
    color:unset;
    text-decoration: none;
    top: 3%;
    height: 15%;
    width: 15%;
    display: flex;
        left: 2%;
    }
`
export default Lightbox
